sqlMap.select:
SELECT SUM(gac.GIS_ASSETS_REAL_RENT_AR), SUM(B.USAGE_AR) FROM gis_assets_cd_f gac left outer join ( SELECT GIS_ASSETS_PRT_AT_CODE, gis_assets_cd, gis_assets_sub_cd, USAGE_AR FROM ASSETS_RENT_DETAIL_F WHERE 1=1
TO_DATE(#searchDt#, 'YYYY-MM-DD') BETWEEN USAGE_PD_FROM AND USAGE_PD_TO
) B ON gac.GIS_ASSETS_PRT_AT_CODE=b.GIS_ASSETS_PRT_AT_CODE and gac.gis_assets_cd=b.gis_assets_cd and gac.gis_assets_sub_cd=B.gis_assets_sub_cd WHERE gac.GIS_ASSETS_USAGE_YN='Y'

SELECT GAM_GETCODENAME('GAM001', gac.GIS_ASSETS_PRPRTY_SE_CD), SUM(gac.GIS_ASSETS_REAL_RENT_AR), SUM(B.USAGE_AR) FROM gis_assets_cd_f gac left outer join ( SELECT GIS_ASSETS_PRT_AT_CODE, gis_assets_cd, gis_assets_sub_cd, USAGE_AR FROM ASSETS_RENT_DETAIL_F WHERE 1=1
TO_DATE(#searchDt#, 'YYYY-MM-DD') BETWEEN USAGE_PD_FROM AND USAGE_PD_TO
) B ON gac.GIS_ASSETS_PRT_AT_CODE=b.GIS_ASSETS_PRT_AT_CODE and gac.gis_assets_cd=b.gis_assets_cd and gac.gis_assets_sub_cd=B.gis_assets_sub_cd WHERE gac.GIS_ASSETS_USAGE_YN='Y' GROUP BY gac.GIS_ASSETS_PRPRTY_SE_CD

SELECT GAM_GETCODENAME('GAM002', gac.GIS_ASSETS_LOC_CD), SUM(gac.GIS_ASSETS_REAL_RENT_AR), SUM(B.USAGE_AR) FROM gis_assets_cd_f gac left outer join ( SELECT GIS_ASSETS_PRT_AT_CODE, gis_assets_cd, gis_assets_sub_cd, USAGE_AR FROM ASSETS_RENT_DETAIL_F WHERE 1=1
TO_DATE(#searchDt#, 'YYYY-MM-DD') BETWEEN USAGE_PD_FROM AND USAGE_PD_TO
) B ON gac.GIS_ASSETS_PRT_AT_CODE=b.GIS_ASSETS_PRT_AT_CODE and gac.gis_assets_cd=b.gis_assets_cd and gac.gis_assets_sub_cd=B.gis_assets_sub_cd WHERE gac.GIS_ASSETS_USAGE_YN='Y' GROUP BY gac.GIS_ASSETS_LOC_CD

SELECT GAM_GETCODENAME('GAM003', gac.GIS_ASSETS_QUAY_CD), SUM(gac.GIS_ASSETS_REAL_RENT_AR), SUM(B.USAGE_AR) FROM gis_assets_cd_f gac left outer join ( SELECT GIS_ASSETS_PRT_AT_CODE, gis_assets_cd, gis_assets_sub_cd, USAGE_AR FROM ASSETS_RENT_DETAIL_F WHERE 1=1
TO_DATE(#searchDt#, 'YYYY-MM-DD') BETWEEN USAGE_PD_FROM AND USAGE_PD_TO
) B ON gac.GIS_ASSETS_PRT_AT_CODE=b.GIS_ASSETS_PRT_AT_CODE and gac.gis_assets_cd=b.gis_assets_cd and gac.gis_assets_sub_cd=B.gis_assets_sub_cd WHERE gac.GIS_ASSETS_USAGE_YN='Y' GROUP BY gac.GIS_ASSETS_QUAY_CD


	select decode(rnum, 7, '기타', MAX(entrps_nm)) entrps_nm, sum(usage_ar)
	from (
	select case when rownum<7 then rownum else 7 end rnum, entrps_nm, USAGE_AR from (
	SELECT entrps_nm, USAGE_AR
	from (
	  SELECT GAM_GETENTRPSNM(A.entrpscd, '1') entrps_nm,SUM(D.USAGE_AR) USAGE_AR FROM ASSETS_RENT_F A, ASSETS_RENT_DETAIL_F D
	  WHERE  1=1
  
	  
	  	TO_DATE(#searchDt#, 'YYYY-MM-DD') BETWEEN USAGE_PD_FROM AND USAGE_PD_TO
	  

	  and a.mng_year=d.mng_year and a.mng_no=d.mng_no and a.mng_cnt=d.mng_cnt
	GROUP BY A.entrpscd
	)
	order by USAGE_AR desc
	)
	)
	group by rnum
