sqlMap.select:
SELECT T.DIR_NO AS DIR_NO ,T.DEPTH_SORT AS DEPTH_SORT ,T.DIR_NM AS DIR_NM ,T.DIR_UPPER_NO FROM ( SELECT 1 AS DIR_NO ,0 AS DEPTH_SORT ,'ROOT' AS DIR_NM ,0 AS DIR_UPPER_NO FROM DUAL UNION ALL SELECT T1.DIR_NO AS DIR_NO ,T1.DEPTH_SORT AS DEPTH_SORT ,T1.DIR_NM AS DIR_NM ,(CASE WHEN T1.DEPTH_SORT > 1 THEN T1.DIR_UPPER_NO ELSE 1 END) AS DIR_UPPER_NO FROM ATCH_FILE_DIR_F T1 WHERE 1 = 1
AND T1.FCLTS_JOB_SE = #dirQueryOption#
) T ORDER BY T.DIR_NO,T.DEPTH_SORT
