sqlMap.select:
SELECT ( SELECT COUNT(*) FROM ASSETS_RENT_F F WHERE F.QUAY_GROUP_CD = 'G'
AND F.GR_USAGE_PD_FROM < SYSDATE AND F.GR_USAGE_PD_TO >= SYSDATE
) currentUseCnt ,( SELECT COUNT(*) FROM LEV_REQEST_F L, ASSETS_RENT_F F WHERE L.PRT_AT_CODE = F.PRT_AT_CODE AND L.MNG_YEAR = F.MNG_YEAR AND L.MNG_NO = F.MNG_NO AND L.MNG_CNT = F.MNG_CNT AND L.NHT_ISUE_YN='N' AND F.QUAY_GROUP_CD = 'G' AND L.NTIC_PD_FROM < SYSDATE+15
) nticPdCnt ,( SELECT COUNT(*) FROM LEV_REQEST_F L, ASSETS_RENT_F F WHERE L.PRT_AT_CODE = F.PRT_AT_CODE AND L.MNG_YEAR = F.MNG_YEAR AND L.MNG_NO = F.MNG_NO AND L.MNG_CNT = F.MNG_CNT AND F.QUAY_GROUP_CD = 'G' AND L.NHT_ISUE_YN = 'Y' AND L.RCIV_SE = '0' ) overDueCnt ,( SELECT COUNT(*) FROM LEV_REQEST_F L, ASSETS_RENT_F F WHERE L.PRT_AT_CODE = F.PRT_AT_CODE AND L.MNG_YEAR = F.MNG_YEAR AND L.MNG_NO = F.MNG_NO AND L.MNG_CNT = F.MNG_CNT AND F.QUAY_GROUP_CD = 'G' AND L.NHT_ISUE_YN = 'Y' AND L.RCIV_SE = '1' ) overNticCnt ,( SELECT COUNT(*) FROM ASSETS_RENT_F F WHERE F.QUAY_GROUP_CD = 'G' AND F.PRMISN_YN='Y' AND F.GR_USAGE_PD_TO >= SYSDATE AND F.GR_USAGE_PD_TO < SYSDATE+30 ) closeEndContract FROM DUAL
