SELECT A.MNG_YEAR, A.MNG_NO, A.MNG_SEQ, A.NTIC_NO, A.PRT_AT_CODE, A.FEE_TP CHRGE_KND_CD, A.CHRGE_KND_NM, A.FISCAL_YR ACCNUT_YEAR, A.BILL_NO, A.ACCNT_CODE, A.ENTRPS_CD, A.ENTRPS_NM, A.BILL_AMNT BILL_AMNT, A.NTIC_DT, TO_CHAR(SYSDATE+15, 'YYYY-MM-DD') DLY_DUE_DT, TO_CHAR(SYSDATE, 'YYYY-MM-DD') DLY_BILL_DT, A.ARRRG_TARIFF, A.ARRRG_NO, A.NEXT_ARRRG_NO, A.RNTFEE, A.PAYINST_INTR, A.SUP_AMT, A.VAT, A.PAY_AMT, U.DLY_BILL_AMNT ARRRG_AMT, U.DLY_BILL_RSN, A.ICHE_STATUS, A.ICHE_STATUS_NM, A.BIZRNO, A.RCIV_SE, A.RSLT_CODE, A.ORIGIN_FEE_TP, A.ORIGIN_FISCAL_YR, A.ORIGIN_BILL_NO ,U.BILL_NO DLY_BILL_NO ,U.DLY_BILL_DT DB_DLY_BILL_DT FROM ( SELECT D.MNG_YEAR ,D.MNG_NO ,D.MNG_SEQ ,D.NTIC_NO ,A.PRT_AT_CODE ,A.FEE_TP ,GAM_GET_FEE_TP_NM(A.PRT_AT_CODE, 'H', A.WORK_CODE) CHRGE_KND_NM ,A.FISCAL_YR ,A.BILL_NO ,A.ACCNT_CODE ,A.AGENT_CODE ENTRPS_CD ,GAM_GETENTRPSNM(A.AGENT_CODE, '1') ENTRPS_NM ,A.BILL_AMNT ,TO_CHAR(A.BILL_DT,'YYYY-MM-DD') NTIC_DT ,TO_CHAR(A.DUE_DATE,'YYYY-MM-DD') DUE_DATE ,D.ARRRG_TARIFF ,D.ARRRG_NO ,NVL(D.ARRRG_NO, 0)+1 AS NEXT_ARRRG_NO ,D.RNTFEE ,D.PAYINST_INTR ,D.SUP_AMT ,D.VAT ,D.PAY_AMT ,A.ICHE_STATUS ,'' ICHE_STATUS_NM ,A.BZ_RGST_ID BIZRNO ,A.RCVD_TP RCIV_SE ,A.RSLT_CODE ,A.ORIGIN_FEE_TP ,A.ORIGIN_FISCAL_YR ,A.ORIGIN_BILL_NO FROM REV_COLL_F A, HTLD_NTIC_DTLS_F D WHERE A.FEE_TP = GAM_GET_HTLD_FEE_TP(D.CHRGE_KND_CD) AND A.WORK_CODE = D.CHRGE_KND_CD AND A.FISCAL_YR = D.ACCNUT_YEAR AND A.BILL_NO = D.NTIC_NO AND A.PRT_AT_CODE = '622' AND D.ACCNUT_YEAR = #accnutYear# AND D.RNTFEE_NTIC_NO = #rntfeeNticNo# AND D.NTIC_SEQ = #nticSeq#
AND D.PAY_TMLMT < TRUNC(SYSDATE) AND (A.RCVD_TP = '0' OR A.RCVD_TP IS NULL) AND A.BILL_AMNT > 0 AND A.BILL_AMNT IS NOT NULL
) A LEFT OUTER JOIN UNPAID_F U ON A.PRT_AT_CODE=U.PRT_AT_CODE AND A.FEE_TP=U.FEE_TP AND A.FISCAL_YR=U.FISCAL_YR AND A.BILL_NO=U.BILL_NO AND U.DLY_SER_NO=LPAD(NVL(A.ARRRG_NO, 0)+1, 2, '0')
