Window.Script:
#include "lib::common.js";

function fnCallback(svcid, errcd, errmsg)
{
	gfn_hideProg(this);
	if(errCd < 0){
		gfn_Message(errCd,errMsg);
		return;
	}
	if(svcid == "getList"){
		//alert("Success");
		div_tot.lbl_tot_cnt.Text = "("+ds_list.GetCount()+")건";
	}
	if(svcid == "insert"){
		alert(errMsg);
		btnSearch_OnClick();
	}	
	if(svcid == "delete"){
		alert(errMsg);
		btnSearch_OnClick();
	}

}


