Window.Script:
#include "lib::common.js";

function fnCallback(svcid, errcd, errmsg)
{
	gfn_hideProg(this);
	if(errCd < 0){
		gfn_Message(errCd,errMsg);
		return;
	}
	if(svcid == "getCode"){
		//alert(ds_combo.SaveXML());
			
	}	
	if(svcid == "getList"){
		//alert("Success");
		div_tot.lbl_tot_cnt.Text = "("+ds_list.GetCount()+")건";
			
	}
	if(svcid == "insert"){
		//alert("Success");
		alert(errMsg);
		btnSearch_OnClick();
	}
	if(svcid == "delete"){
		//alert("Success");
		alert(errMsg);
		btnSearch_OnClick();
	}
	
}


function btnSearch_OnClick(obj)
{
	if ( ds_list.GetCount() > 0 ) gfn_GridSort(grdList, -1);
	gfn_showProg(this,"조회중입니다.");
	ds_input.ClearData();
	ds_input.AddRow();
	ds_input.SetColumn(0,"searchCondition",1);
	ds_input.SetColumn(0,"searchKeyword",txtSearchKeyword.Text);


	var strSvcid = "getList";
	var strController = "/ipa/cmm/sec/ram/ISDeptAuthorList.do";
	//var strVoClass = "egovframework.com.cmm.ComDefaultVO";
	//var strInputDs = "ds_input=ds_input";
	
	var arrayInputDs = [
						["ds_input","ds_input","egovframework.com.cmm.ComDefaultVO"]
					   ];
					   	
	var strOutputDs = "ds_list=ds_output";
	var strParam = "";
	var strFnCallback = "fnCallback";

	fnCmTr(this, strSvcid, strController, arrayInputDs, strOutputDs, strParam, strFnCallback);
}

function btnInsert_OnClick(obj)
{
	gfn_showProg(this,"저장중입니다.");
	var userIds = "";
	var authorCodes = "";
	var regYns = "";
	var uniqIds = "";
	var oldauthorCodes = "";
		
 	for (i=0; i<ds_list.RowCount(); i++){
		if ( ds_list.GetColumn(i, "delChk") == 1 ){
			uniqIds += ds_list.GetColumn(i, "uniqId")+";";
			authorCodes+= ds_list.GetColumn(i, "authorCode")+";";
			regYns += ds_list.GetColumn(i, "regYn")+";";			
			userIds += ds_list.GetColumn(i, "userId")+";";
			oldauthorCodes += ds_list.GetOrgColumn(i, "authorCode")+";";			
		}
 	}

	
	var param = "uniqIds=" + uniqIds;
	param += "&authorCodes=" + authorCodes;
	param += "&regYns=" + regYns;
	param += "&userIds=" + userIds;
	param += "&oldauthorCodes=" + oldauthorCodes;
	 	
	var strSvcid = "insert";
	var strController = "/ipa/cmm/sec/ram/ISDeptAuthorInsert.do?"+param;
	var arrayInputDs = [];
	var strOutputDs = "";
	var strParam = "";
	var strFnCallback = "fnCallback";

	fnCmTr(this, strSvcid, strController, arrayInputDs, strOutputDs, strParam, strFnCallback);

}


function grdList_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if (grdList.GetCellProp("head",nCell,"text")=='selChk'
		|| grdList.GetCellProp("head",nCell,"text")=='0'
		|| grdList.GetCellProp("head",nCell,"text")=='1'
	){
		lfn_GridSelChk(obj, "delChk");
	}else{
		gfn_GridSort(obj, nCell);
	}
}
function btnPopup_OnClick(obj)
{
	
	var iXPos = ClientToScreenX(obj, 0);
	var iyPos = ClientToScreenY(obj, obj.height);

	var url=G_Server+"/ipa/cmm/sec/ram/ISDeptSearchView.do";
	var divleft = -1;
	var divtop = -1;
	var objname = "groupDiv";
	if(!isValidObject(object(objname))){
		Create("PopupDiv", objname ,'width="400" height="300" border="Flat" url="'+url+'"'); 
		object(objname).TrackPopup(iXPos, iyPos);		
	}else{
		object(objname).TrackPopup(iXPos, iyPos);		
	}
	
}

function btnDelete_OnClick(obj)
{
	if ( !confirm("선택한 항목들(을)를 삭제 하시겠습니까?") ) return;
	
	gfn_showProg(this,"삭제중입니다.");
	var userIds = "";
	var uniqIds = "";
	var authorCodes = "";	
 	for (i=0; i<ds_list.RowCount(); i++){
		if ( ds_list.GetColumn(i, "delChk") == 1 ){
			uniqIds += ds_list.GetColumn(i, "uniqId")+";";
			userIds += ds_list.GetColumn(i, "userId")+";";
			authorCodes += ds_list.GetColumn(i, "authorCode")+";";	
		}
 	}
 	var param = "uniqIds=" + uniqIds;
 	param += "&authorCodes=" + authorCodes;
 	param += "&userIds=" + userIds;

	 	
	var strSvcid = "delete";
	var strController = "/ipa/cmm/sec/ram/ISDeptAuthorDelete.do?"+param;
	var arrayInputDs = [];
	var strOutputDs = "";
	var strParam = "";
	var strFnCallback = "fnCallback";

	fnCmTr(this, strSvcid, strController, arrayInputDs, strOutputDs, strParam, strFnCallback);
}

function ISDeptAuthorManage_OnLoadCompleted(obj)
{

	gfn_showProg(this,"조회중입니다.");

	var strSvcid = "getCode";
	var strController = "/ipa/cmm/sec/ram/ISDeptAuthorCode.do";

	var arrayInputDs = [];
					   	
	var strOutputDs = "ds_combo=ds_outputauthor";
	var strParam = "";
	var strFnCallback = "fnCallback";

	fnCmTr(this, strSvcid, strController, arrayInputDs, strOutputDs, strParam, strFnCallback);	

}


function txtSearchKeyword_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if (nChar==13) btnSearch_OnClick();
}
