Window.Script:
#include "lib::common.js";

function fnCallback(svcid, errcd, errmsg)
{

	gfn_hideProg(this);
	if(errCd < 0){
		gfn_Message(errCd,errMsg);
		return;
	}
	if(svcid == "update"){
		parent.btnCurrent_OnClick();
		btnClose_OnClick();
	}
}

function btnClose_OnClick(obj)
{
	close();
}

function btnInsert_OnClick(obj)
{
	
	if ( !gfn_CheckField("Edit1|Edit2|TextArea0|cmbSearchCondition", "분류코드|분류코드명|분류코드설명|사용여부") ) return false;
	
	gfn_showProg(this,"수정중입니다.");
	var strSvcid = "update";
	var strController = "/ipa/sym/ccm/ccc/ISCcmCmmnClCodeModify.do";
	var arrayInputDs = [
						["ds_input","ds_input","egovframework.com.sym.ccm.ccc.service.CmmnClCode"]
					   ];
	var strOutputDs = "";
	var strParam = "";
	var strFnCallback = "fnCallback";

	fnCmTr(this, strSvcid, strController, arrayInputDs, strOutputDs, strParam, strFnCallback);		
}

function ISCcmCmmnClCodeModify_OnLoadCompleted(obj)
{
	gfn_showProg(this,"조회중입니다.");
	
	ds_input.ClearData();
	ds_input.AddRow();
	ds_input.SetColumn(0,"clCode",parent.ds_list.GetColumn(parent.grdList.CurrentRow,"clCode"));
	
	var strSvcid = "get";
	var strController = "/ipa/sym/ccm/ccc/ISCcmCmmnClCodeDetail.do";

	var arrayInputDs = [
						["ds_input","ds_input","egovframework.com.sym.ccm.ccc.service.CmmnClCode"]
					   ];
					   	
	var strOutputDs = "ds_input=ds_output";
	var strParam = "";
	var strFnCallback = "fnCallback";

	fnCmTr(this, strSvcid, strController, arrayInputDs, strOutputDs, strParam, strFnCallback);
}



function ImgClose_OnClick(obj,nX,nY)
{
	close();
}
