Window.Script:
#include "lib::common.js";

function fnCallback(svcid, errcd, errmsg)
{
	gfn_hideProg(this);
	if(errCd < 0){
		gfn_Message(errCd,errMsg);
		return;
	}
	if(svcid == "update"){
		parent.btnCurrent_OnClick();
		btnClose_OnClick();
	}
}

function btnClose_OnClick(obj)
{
	close();
}

function btnInsert_OnClick(obj)
{
	if ( !gfn_CheckField("Edit2|Edit0|Edit1|TextArea0|cmbSearchCondition", "분류코드명|코드ID|코드ID명|코드ID설명|사용여부") ) return false;
	
	gfn_showProg(this,"수정중입니다.");
	var strSvcid = "update";
	var strController = "/ipa/sym/ccm/cca/ISCcmCmmnCodeModify.do";
	var arrayInputDs = [
						["ds_input","ds_input","egovframework.com.sym.ccm.cca.service.CmmnCode"]
					   ];
	var strOutputDs = "";
	var strParam = "";
	var strFnCallback = "fnCallback";

	fnCmTr(this, strSvcid, strController, arrayInputDs, strOutputDs, strParam, strFnCallback);		
}

function ISCcmCmmnCodeModify_OnLoadCompleted(obj)
{


	gfn_showProg(this,"조회중입니다.");
	
	ds_input.ClearData();
	ds_input.AddRow();
	ds_input.SetColumn(0,"codeId",parent.ds_list.GetColumn(parent.grdList.CurrentRow,"codeId"));
	
	var strSvcid = "get";
	var strController = "/ipa/sym/ccm/cca/ISCcmCmmnCodeDetail.do";

	var arrayInputDs = [
						["ds_input","ds_input","egovframework.com.sym.ccm.cca.service.CmmnCode"]
					   ];
					   	
	var strOutputDs = "ds_input=ds_output";
	var strParam = "";
	var strFnCallback = "fnCallback";

	fnCmTr(this, strSvcid, strController, arrayInputDs, strOutputDs, strParam, strFnCallback);	
	
}

function ImgClose_OnClick(obj,nX,nY)
{
	close();
}
