Window.Script:
#include "lib::common.js";

function fnCallback(svcid, errcd, errmsg)
{
	gfn_hideProg(this);
	if(errCd < 0){
		gfn_Message(errCd,errMsg);
		return;
	}
	if(svcid == "update"){
		parent.btnCurrent_OnClick();		
		btnClose_OnClick();
	}
}

function btnClose_OnClick(obj)
{
	close();
}

function btnInsert_OnClick(obj)
{
	if ( !gfn_CheckField("Edit1|TextArea0|cmbSearchCondition", "코드명|코드설명|사용여부") ) return false;
	
	gfn_showProg(this,"수정중입니다.");
	var strSvcid = "update";
	var strController = "/ipa/sym/ccm/cde/ISCcmCmmnDetailCodeModify.do";
	var arrayInputDs = [
						["ds_input","ds_input","egovframework.com.sym.ccm.cde.service.CmmnDetailCode"]
					   ];
	var strOutputDs = "";
	var strParam = "";
	var strFnCallback = "fnCallback";

	fnCmTr(this, strSvcid, strController, arrayInputDs, strOutputDs, strParam, strFnCallback);		
}



function ISCcmCmmnDetailCodeModify_OnLoadCompleted(obj)
{

	gfn_showProg(this,"조회중입니다.");
	
	ds_input.ClearData();
	ds_input.AddRow();
	ds_input.SetColumn(0,"codeId",parent.ds_list.GetColumn(parent.grdList.CurrentRow,"codeId"));
	ds_input.SetColumn(0,"code",parent.ds_list.GetColumn(parent.grdList.CurrentRow,"code"));
	//alert(ds_input.SaveXML());
	var strSvcid = "get";
	var strController = "/ipa/sym/ccm/cde/ISCcmCmmnDetailCodeDetail.do";

	var arrayInputDs = [
						["ds_input","ds_input","egovframework.com.sym.ccm.cde.service.CmmnDetailCode"]
					   ];
					   	
	var strOutputDs = "ds_input=ds_output";
	var strParam = "";
	var strFnCallback = "fnCallback";

	fnCmTr(this, strSvcid, strController, arrayInputDs, strOutputDs, strParam, strFnCallback);
}

function ImgClose_OnClick(obj,nX,nY)
{
	close();
}
