Window.Script:
#include "lib::common.js";
var searchKeyword;
var searchCondition;
function fnCallback(svcid, errcd, errmsg)
{
	gfn_hideProg(this);
	if(errCd < 0){
		gfn_Message(errCd,errMsg);
		return;
	}
	if(svcid == "get"){
		//alert(ds_output.SaveXML());
		//alert("Success");
	}
	if(svcid == "update"){
		//alert("Success");
	}
	if(svcid == "delete"){
		alert(errMsg);	
		parent.btnSearch_OnClick();
		imgClose_OnClick();
	}	
}

function ISCcmZipDetail_OnLoadCompleted(obj)
{
	gfn_showProg(this,"조회중입니다.");
	searchKeyword  	= gdsPARAM.GetColumn(0,"PARAM_VALUE");
	searchCondition = gdsPARAM.GetColumn(1,"PARAM_VALUE");
	var strSvcid = "get";
	var strController = "/ipa/sym/cmm/zip/ISCcmZipDetail.do?req_zip="+gdsPARAM.GetColumn(2,"PARAM_VALUE")+"&req_sn="+gdsPARAM.GetColumn(3,"PARAM_VALUE");
	var arrayInputDs = [];
	var strOutputDs = "ds_output=ds_output";
	var strParam = "";
	var strFnCallback = "fnCallback";

	fnCmTr(this, strSvcid, strController, arrayInputDs, strOutputDs, strParam, strFnCallback);
}

function btnList_OnClick(obj)
{
	gdsPARAM.ClearData();
	gdsPARAM.AddRow();
	gdsPARAM.SetColumn(0,"PARAM_NAME","searchKeyword");
	gdsPARAM.SetColumn(0,"PARAM_VALUE",searchKeyword);
	gdsPARAM.AddRow();
	gdsPARAM.SetColumn(1,"PARAM_NAME","searchCondition");
	gdsPARAM.SetColumn(1,"PARAM_VALUE",searchCondition);
	Go(G_Server + "/ipa/sym/cmm/zip/ISCcmZipListView.do");
	
}

function btnDelete_OnClick(obj)
{
	if ( !confirm(txtZip.Text + "(을)를 삭제 하시겠습니까?") ) return;
	
	gfn_showProg(this,"삭제중입니다.");
		
		
	var strSvcid = "delete";
	var strController = "/ipa/sym/cmm/zip/ISCcmZipRemove.do";
		
	var arrayInputDs = [["ds_output","ds_output","egovframework.com.sym.ccm.zip.service.Zip"]];
							
	var strOutputDs = "";
	var strParam = "";
	var strFnCallback = "fnCallback";
	
	fnCmTr(this, strSvcid, strController, arrayInputDs, strOutputDs, strParam, strFnCallback);
}



function btnUpdate_OnClick(obj)
{
	//Go(G_Server + "/ipa/sym/cmm/zip/ISCcmZipModifyView.do");
	
	var url=G_Server+"/ipa/sym/cmm/zip/ISCcmZipModifyView.do";

	var iXPos = ClientToScreenX(this, 0);
	var iyPos = ClientToScreenY(this, 30);

	Dialog(url,"",400,300,"",iXPos,iyPos);	
	close();
}

function imgClose_OnClick(obj,nX,nY)
{
	close();
}
