Window.Script:
#include "lib::common.js";

function fnCallback(svcid, errcd, errmsg)
{
	gfn_hideProg(this);
	if(errCd < 0){
		gfn_Message(errCd,errMsg);
		return;
	}
	if(svcid == "getList"){
		
		//alert("Success");
	}

}


function btnSearch_OnClick(obj)
{
	if(Trim(txtSearchKeyword.Text) == null) {
		//Alert("동을 입력하세요");
		txtSearchKeyword.SetFocus();
	} else{
		//if ( ds_output.GetCount() > 0 ) gfn_GridSort(grdList, -1);
		gfn_showProg(this,"조회중입니다.");
		ds_input.ClearData();
		ds_input.AddRow();
		ds_input.SetColumn(0,"searchKeyword",txtSearchKeyword.Text);
		ds_input.SetColumn(0,"searchCondition","4");
		
		var strSvcid = "getList";
		var strController = "/ipa/sym/cmm/zip/ISCcmZipList.do";
		
		var arrayInputDs = [
							["ds_input","ds_input","egovframework.com.sym.ccm.zip.service.ZipVO"]
						   ];
							
		var strOutputDs = "ds_output=ds_output";
		var strParam = "";
		var strFnCallback = "fnCallback";
	
		fnCmTr(this, strSvcid, strController, arrayInputDs, strOutputDs, strParam, strFnCallback);
	}
}


function grdList_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	parent.txtZip.Text = ds_output.GetColumn(nRow,"zip");
	parent.txtAdres.Text =	trim(ds_output.GetColumn(nRow,"ctprvnNm"))
						+" " +trim(ds_output.GetColumn(nRow,"signguNm"))
						+" " +trim(ds_output.GetColumn(nRow,"emdNm"))
						+" " +trim(ds_output.GetColumn(nRow,"liBuldNm"))
						+" " +trim(ds_output.GetColumn(nRow,"lnbrDongHo"));
	ImgClose_OnClick();
}

function ImgClose_OnClick(obj,nX,nY)
{
	//this.ClosePopup();
	close();
}

function txtSearchKeyword_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if (nChar==13) btnSearch_OnClick();
}

function grdList_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	gfn_GridSort(obj, nCell);
}
