Window.Script:
function Debug_OnLoadCompleted(obj)
{
	var lv_cts = "";
	var lv_col = 0;
	var lv_colid;
	
	var dsObj = object(DS_NAME);

	lv_cts += "<contents>\n";
	lv_cts += "	<format id='Default'>\n";
	lv_cts += "		<columns>\n";
	for(var i=0; i<dsObj.colcount; i++)
		lv_cts += "			<col width='80'/>\n";
	lv_cts += "		</columns>\n";
	lv_cts += "		<head>\n";
	for(var i=0; i<dsObj.colcount; i++)
	{
		lv_colid = dsObj.GetColID(dsObj.GetColIDXbyorder(i));
		lv_cts += "		<cell bkcolor='user9' col='" + lv_col + "' color='user8' display='text' text='" + lv_colid + "'/>\n";
		lv_col++;
	}
	lv_cts += "		</head>\n";
	lv_cts += "		<body>\n";
	lv_col = 0;
	for(var i=0; i<dsObj.colcount; i++)
	{
		lv_colid = dsObj.GetColID(dsObj.GetColIDXbyorder(i));
		lv_cts += "			<cell col='" + lv_col + "' colID='" + lv_colid + "' color='user8'  display='text'/>\n";
		lv_col++;
	}
	lv_cts += "		</body>\n";
	lv_cts += "	</format>\n";
	lv_cts += "</contents>";
	
	if(dsObj.colcount>8)	grd_data.AutoFit = false;
	else					grd_data.AutoFit = true;
	lbl_count.Text = "(데이터 갯수 : " + dsObj.rowcount + " 건)";
	lbl_dsname.Text = DS_NAME;
	grd_data.Contents = lv_cts;
	grd_data.BindDataset = DS_NAME;
}

function btn_close_OnClick(obj)
{
	Close();
}
